--
-- Cấu trúc bảng cho bảng `hv_bgs`
--

CREATE TABLE `hv_bgs` (
  `hv_bg_id` int(10) UNSIGNED NOT NULL,
  `ngayThamGia` date NOT NULL,
  `hocVien_id` int(10) UNSIGNED DEFAULT NULL,
