INSERT INTO `tintucs` (`tinTuc_id`, `tenTinTuc`, `moTa`, `tomTat`, `noiDung`, `Hinh`, `ngayDang`, `hocVien_id`, `giangVien_id`, `created_at`, `updated_at`) VALUES
(1, 'Họp online bằng giải pháp Jitsi nội địa để tránh lệ thuộc dịch vụ nước ngoài', 'Tin công nghệ (Nguồn: ICTNews)', 'Mới đây, Cục An toàn thông tin, Bộ TT&TT đã khuyến nghị các cơ quan, tổ chức hành chính nhà nước không nên dùng Zoom; các cá nhân, tổ chức khác cũng cần cẩn trọng khi sử dụng. Trước đó, nhiều quốc gia cũng đã có những khuyến nghị tương tự. Ngoài ra, cũng có nhiều đánh giá chuyên môn, nêu quan điểm quan ngại về vấn đề an toàn, bảo mật, riêng tư của người dùng khi sử dụng Zoom.', 'Giải pháp để không phụ thuộc dịch vụ nước ngoài\r\n\r\nMới đây, Cục An toàn thông tin, Bộ TT&TT đã khuyến nghị các cơ quan, tổ chức hành chính nhà nước không nên dùng Zoom; các cá nhân, tổ chức khác cũng cần cẩn trọng khi sử dụng. Trước đó, nhiều quốc gia cũng đã có những khuyến nghị tương tự. Ngoài ra, cũng có nhiều đánh giá chuyên môn, nêu quan điểm quan ngại về vấn đề an toàn, bảo mật, riêng tư của người dùng khi sử dụng Zoom.\r\n\r\nLiên minh 5 doanh nghiệp nguồn mở Việt mới đây đã công bố các giải pháp hỗ trợ họp online trên nền Jitsi khá toàn diện và linh hoạt; từ tư vấn, thiết kế theo nhu cầu, triển khai trên hạ tầng sẵn có của khách hàng đến cung cấp cả gói hạ tầng riêng, bảo trì, hỗ trợ vận hành, tích hợp hệ thống. Chi phí các gói giải pháp dành cho tổ chức, doanh nghiệp ước tính từ 349 triệu đồng cho một lần triển khai và được sử dụng, hỗ trợ bảo trì vĩnh viễn.\r\n\r\nViệc họp trực tuyến qua video đòi hỏi băng thông lớn. Lưu lượng đường truyền Internet ra quốc tế của Việt Nam còn giới hạn và thường xuyên gặp phải các sự cố cáp quang biển. Các dịch vụ nước ngoài có thể đặt hạ tầng máy chủ ở nhiều nơi trên thế giới, do đó việc sử dụng dịch vụ sẽ mất chủ động về định tuyến, dẫn tới thiếu ổn định trong cung cấp dịch vụ.\r\n\r\nCác chuyên gia cho rằng, doanh nghiệp ICT trong nước hoàn toàn có thể hóa giải bài toán trên bằng cách cung cấp giải pháp triển khai trên máy chủ đặt tại cơ quan, tổ chức và đặt tại ISP trong nước, không phụ thuộc đường truyền quốc tế. Từ đó có thể loại bỏ rủi ro đứt cáp quang biển, tăng cường an ninh và đảm bảo hiệu năng.\r\n\r\nVề chất lượng hình ảnh, bộ định tuyến VideoBridge của Jitsi được phát triển theo công nghệ SFU với mô-đun Jicofo được cải tiến, giúp lựa chọn chất lượng hình ảnh từ một đầu cuối theo mức độ ưu tiên một cách thông minh hơn.\r\n\r\nCông nghệ SFU truyền hình ảnh trực tiếp giữa các máy đầu cuối, không đòi hỏi máy chủ cấu hình cao, trong khi vẫn đảm bảo được chất lượng hình ảnh khi họp với số lượng lớn. Phía đầu cuối, Jitsi sử dụng WebRTC hỗ trợ những công nghệ nén hình ảnh tiên tiến VP8 hoặc H.264 và sẵn sàng hỗ trợ công nghệ mới nhất VP9, đảm bảo chất lượng hình ảnh trong nhiều điều kiện tốc độ kết nối đa dạng.\r\n\r\nNgoài ra, trong lựa chọn nâng cao, tổ chức, doanh nghiệp có thể tích hợp với các hệ thống họp online có sẵn khác như Polycom, CISCO, Granstream, Yealink, tổng đài CCALL Cloud, SIP, chữ ký số và các hệ thống nghiệp vụ (dịch vụ công, một cửa điện tử, điều hành tác nghiệp)...\r\n\r\nNền tảng Jitsi được ứng dụng rộng rãi\r\n\r\nCác giải pháp họp online của CoMeet được phát triển trên nền tảng mã nguồn mở Jitsi. Đây là nền tảng được đánh giá có độ bảo mật, ổn định tốt và có tính khả khi cao.\r\n\r\nKhông chỉ sử dụng đơn giản, nền tảng nguồn mở từ lâu đã khẳng định nhiều ưu điểm đáng lưu tâm. Tiến sĩ Nguyễn Hồng Quang, Chủ tịch Câu lạc bộ Phần mềm tự do nguồn mở Việt Nam (VFOSSA) cho biết, việc triển khai các giải pháp trên nền tảng phần mềm nguồn mở đem lại những lợi ích lớn cho nhà phát triển, triển khai và cả đơn vị ứng dụng giải pháp công nghệ. Với Jitsi, đây là nền tảng được xây dựng trên cơ sở các giao thức truyền thông tiên tiến nhất và liên tục được phát triển dựa trên đóng góp của cộng đồng chuyên gia toàn cầu.\r\n\r\nCác kỹ sư nguồn mở Việt Nam vốn được xem là am hiểu những vấn đề liên quan đến giấy phép bản quyền, quy tắc ứng xử, giao tiếp trong cộng đồng do có thời gian dài trực tiếp tham gia vào các công đoạn phát triển kỹ thuật của dự án phần mềm, từ phân tích yêu cầu, thiết kế hệ thống, lập trình, kiểm thử, xây dựng tài liệu… Do đó, họ có thể dễ dàng thiết kế kiến trúc, lựa chọn hạ tầng phù hợp, kiểm soát các vấn đề, sửa lỗi khi cần thiết, tăng cường đảm bảo an toàn, an ninh và bảo mật cho hệ thống.\r\n\r\nBên cạnh đó, mã nguồn mở cũng được rà soát ngang hàng liên tục để tăng cường mức độ an toàn, giảm thiểu khả năng tồn tại những lỗi tiềm ẩn, gây mất an toàn, an ninh, kể cả những lỗi “cố ý” do nhà phát triển thực hiện với mục đích xấu. Điều này đặc biệt quan trọng với các đơn vị ứng dụng là cơ quan nhà nước, các doanh nghiệp, tổ chức lớn, có mức độ ảnh hưởng lớn trong xã hội hoặc các nhóm đối tượng “nhạy cảm”, dễ bị ảnh hưởng như các đối tượng yếu thế, trẻ em…\r\n\r\nTùy biến theo thiết kế nhưng chưa đáp ứng hệ thống “siêu lớn”\r\n\r\nMặc dù được kỳ vọng về chất lượng nhưng về số lượng, theo đánh giá của các chuyên gia, các giải pháp nội địa hiện vẫn chưa thể so sánh với sản phẩm nước ngoài như Zoom.\r\n\r\nTrao đổi với PV VietNamNet, ông Vũ Thế Bình, Tổng thư ký Hiệp hội Internet Việt Nam cho rằng, các dịch vụ, giải pháp trong nước chưa thể sẵn sàng thay thế ngay các giải pháp nước ngoài trong tình trạng nhu cầu tăng đột ngột và khó lường trước như hiện nay. Hơn nữa, những dịch vụ nền tảng cho đến nay, phần lớn thị phần vẫn nằm trong tay các tập đoàn toàn cầu. Các nền tảng này có bề dày, sức mạnh nguồn lực, sức mạnh hệ sinh thái mà không dễ để chúng ta có thể xây dựng ngay được.\r\n\r\nCũng theo ông Bình, hiện đã có nhiều doanh nghiệp công bố các giải pháp, dịch vụ có khả năng thay thế dịch vụ từ nước ngoài. Điều này ít nhiều giảm nhiệt cho các nhu cầu, bức xúc của người dùng trong nước. “Tuy nhiên, chắc chắn rằng vẫn cần thêm một thời gian nữa mới đánh giá được liệu các dịch vụ, giải pháp trong nước có thể thay thế được hoàn toàn các dịch vụ từ nước ngoài hay không”, ông Bình nhận định.\r\n\r\nĐại diện CoMeet cũng cho hay: “Các giải pháp do liên minh cung cấp cần thêm thời gian để có thể cung cấp dưới dạng dịch vụ trên nền điện toán đám mây (Cloud Computing) cho các đối tượng có nhu cầu sử dụng với quy mô lớn như các trường học và các tổ chức, doanh nghiệp lớn”.\r\n\r\nMặt khác, các giải pháp do liên minh doanh nghiệp Việt đưa ra còn phải đối diện nhiều khó khăn trong tiếp cận các cơ quan nhà nước và các tổ chức, doanh nghiệp lớn. Các chuyên gia cho rằng cần có các chính sách hỗ trợ về ưu đãi và tạo thị trường từ cơ quan quản lý nhà nước để dần hình thành các dịch vụ Internet nội địa, với điều kiện phải hoàn toàn làm chủ công nghệ, không phụ thuộc nhà cung cấp nước ngoài và có thể cung cấp ở quy mô lớn.\r\nNhìn nhận khách quan, hiện có nhiều doanh nghiệp ICT trong nước có khả năng cung cấp dịch vụ họp trực tuyến qua video. Tuy vậy, vẫn chưa có nhà cung cấp nào đủ quy mô lớn với mô hình điện toán đám mây để thay thế hoàn toàn các dịch vụ nước ngoài như Zoom, MS Teams, Cisco Webex, Google Meet. ', '1_hop_online_3_2.jpg', '2020-03-19', NULL, NULL, NULL, NULL),
(2, 'Thủ tướng yêu cầu Bộ Công an báo cáo về đề xuất lùi thời hạn khai thác CSDL quốc gia về dân cư', 'Tin công nghệ (Nguồn: ICTNews)', 'Thủ tướng yêu cầu Bộ Công an báo cáo về việc đề xuất Ủy ban Thường vụ Quốc hội lùi thời hạn khai thác Cơ sở dữ liệu quốc gia về dân cư theo quy định của Luật Căn cước công dân cho đến khi hoàn thành xây dựng Cơ sở dữ liệu quốc gia về dân cư.', 'Thủ tướng yêu cầu Bộ Công an báo cáo về việc đề xuất Ủy ban Thường vụ Quốc hội lùi thời hạn khai thác Cơ sở dữ liệu quốc gia về dân cư theo quy định của Luật Căn cước công dân cho đến khi hoàn thành xây dựng Cơ sở dữ liệu quốc gia về dân cư.\r\n\r\nThủ tướng Nguyễn Xuân Phúc vừa yêu cầu Bộ Công an, Bộ Tư pháp tiếp tục triển khai cấp số định danh cá nhân tại các địa phương theo đúng quy định của Luật Căn cước công dân, Luật Hộ tịch và các văn bản hướng dẫn thi hành.\r\n\r\nThủ tướng chỉ đạo, trong quá trình nhập dữ liệu công dân, Bộ Công an phối hợp chặt chẽ với Bộ Tư pháp và chính quyền các địa phương để bảo đảm thông tin chính xác. Đối với các trường hợp thiếu, sai sót thông tin, Bộ Công an, Bộ Tư pháp chỉ đạo cơ quan, đơn vị có liên quan thông tin cho công dân và phối hợp chặt chẽ với công dân trong đính chính, sửa đổi, bổ sung thông tin còn thiếu hoặc chưa chính xác trước khi cập nhật vào Cơ sở dữ liệu quốc gia về dân cư.\r\n\r\nBộ Công an báo cáo Thủ tướng Chính phủ về việc đề xuất Ủy ban Thường vụ Quốc hội lùi thời hạn khai thác Cơ sở dữ liệu quốc gia về dân cư theo quy định của Luật Căn cước công dân cho đến khi hoàn thành xây dựng Cơ sở dữ liệu quốc gia về dân cư. Bộ Công an giải trình cụ thể các vấn đề liên quan trong tháng 4/2020. \r\n\r\nThủ tướng cũng yêu cầu các bộ, ngành, địa phương khi thực hiện các nhiệm vụ của Đề án 896, xây dựng các cơ sở dữ liệu chuyên ngành cần phối hợp chặt chẽ, hiệu quả với Bộ Công an và các bộ, ngành, cơ quan, tổ chức có liên quan để việc xây dựng bảo đảm đồng bộ, thống nhất, tránh chồng chéo, trùng lắp, xác định các thông tin, dữ liệu cần thiết thu thập. Đồng thời phải tương thích, kết nối, tích hợp được với các cơ sở dữ liệu và kết nối với Cơ sở dữ liệu quốc gia về dân cư và có giải pháp bảo vệ bí mật thông tin cá nhân.\r\n\r\nHồi giữa tháng 3 vừa qua, Thủ tướng Nguyễn Xuân Phúc đã phê duyệt điều chỉnh chủ trương đầu tư dự án Cơ sở dữ liệu quốc gia về dân cư. Tổng mức đầu tư của dự án này là hơn 3.085 tỷ đồng từ nguồn vốn ngân sách nhà nước và được thực hiện từ năm 2018-2021.\r\n\r\nTheo quyết định, chủ đầu tư dự án là Cục Cảnh sát quản lý hành chính về trật tự xã hội, Bộ Công an. Mục tiêu của dự án nhằm xây dựng Cơ sở dữ liệu quốc gia về dân cư thống nhất trên toàn quốc, tập hợp thông tin cơ bản của tất cả công dân Việt Nam được chuẩn hóa, số hóa, lưu trữ, quản lý bằng cơ sở hạ tầng thông tin để phục vụ quản lý nhà nước và giao dịch của cơ quan, tổ chức, cá nhân.\r\n\r\nVề quy mô, nội dung đầu tư, Quyết định nêu rõ, xây dựng hệ thống Cơ sở dữ liệu quốc gia về dân cư thống nhất trên toàn quốc, được triển khai và quản lý tập trung tại Bộ Công an, bao gồm: Đầu tư hạ tầng kỹ thuật; thuê hạ tầng truyền dẫn; xây dựng, triển khai hệ thống phần mềm ứng dụng phục vụ công tác quản lý nhà nước và các yêu cầu chức năng, nghiệp vụ của ngành Công an phục vụ thu thập, cập nhật thông tin; tổ chức thu thập, tạo lập và chuyển đổi dữ liệu dân cư và cấp số định danh cá nhân cho công dân.\r\n\r\nQuyết định cũng cho biết, Bộ Công an sẽ thực hiện việc đào tạo về hạ tầng kỹ thuật, đào tạo sử dụng phần mềm ứng dụng, tuyên truyền, phổ biến pháp luật, chính sách liên quan đến quản lý dân cư trên toàn quốc.\r\n\r\nCơ sở dữ liệu quốc gia về dân cư là một trong 6 cơ sở dữ liệu quốc gia cần ưu tiên triển khai để tạo nền tảng phát triển chính phủ điện tử, bên cạnh 5 cơ sở dữ liệu quốc gia khác gồm: Đất đai quốc gia; Đăng ký doanh nghiệp; Thống kê tổng hợp về Dân số; và Bảo hiểm.\r\n\r\nTheo đánh giá của Thủ tướng Chính phủ, cơ sở dữ liệu quốc gia về dân cư có vai trò quyết định trong nâng cao hiệu quả quản lý nhà nước về dân cư theo hướng hiện đại, là cơ sở dữ liệu quan trọng trong tiến trình xây dựng chính phủ điện tử hướng tới nền kinh tế số, xã hội số.\r\n\r\nThủ tướng Chính phủ giao Bộ Công an phối hợp với Bộ TT&TT rà soát các dự án xây dựng cơ sở dữ liệu được triển khai tại Bộ Công an theo hướng tận dụng các nền tảng kỹ thuật công nghệ, hạ tầng dùng chung để giảm chi phí đầu tư. Bộ KH&ĐT chủ trì thẩm định, phối hợp Bộ Công an đề xuất Thủ tướng Chính phủ điều chỉnh chủ trương đầu tư dự án theo đúng quy định của Luật Đầu tư công.', '2_the-can-cuoc-cong-dan.jpg', '2020-03-20', NULL, NULL, NULL, NULL),
(3, 'Hà Nội: Học sinh không được công khai địa chỉ, mật khẩu các lớp học trực tuyến', 'Tin công nghệ (Nguồn: ICTNews)', 'Trong công văn của Sở GD&ĐT Hà Nội, học sinh tham gia các lớp học trực tuyến không được công bố công khai địa chỉ, mật khẩu của lớp học. Khi có tình huống bất thường, giáo viên, học sinh, cha mẹ học sinh cần cung cấp thông tin đến cơ quan quản lý và công an để điều tra và xử lý.', 'Trong công văn của Sở GD&ĐT Hà Nội, học sinh tham gia các lớp học trực tuyến không được công bố công khai địa chỉ, mật khẩu của lớp học. Khi có tình huống bất thường, giáo viên, học sinh, cha mẹ học sinh cần cung cấp thông tin đến cơ quan quản lý và công an để điều tra và xử lý.\r\n\r\nCụ thể, Sở Giáo dục và Đào tạo (GD&ĐT) thành phố Hà Nội vừa có văn bản gửi đến Phòng GD&ĐT và các cơ sở đào tạo trên địa bàn thành phố về việc tăng cường công tác bảo đảm an toàn cho học sinh trong quá trình học tập qua Internet.\r\n\r\nVăn bản của Sở nêu rõ,  để phù hợp với tình hình học sinh phải nghỉ học do phòng, chống dịch Covid-19 trong thời gian qua, Sở GD&ĐT đã triển khai hệ thống ôn tập trực tuyến tại địa chỉ http://study.hanoi.edu.vn cho học sinh, đồng thời phối hợp cùng Đài PT&TH Hà Nội tổ chức dạy học trên truyền hình cho học sinh từ lớp 4 đến lớp 12.\r\n\r\nĐể đảm bảo an toàn cho học sinh, sinh viên trong quá trình học tập từ xa, Sở GD&ĐT đề nghị các phòng GD&ĐT, các đơn vị trực thuộc Sở tổ chức tuyên truyền, tập huấn phổ biến kiến thức, kĩ năng sử dụng Internet, mạng xã hội đảm bảo an toàn, an ninh mạng khi tham gia hoạt động dạy - học qua Internet.\r\n\r\nGiới thiệu cho giáo viên những giải pháp, phần mềm quản lý, tổ chức dạy học qua Internet tin cậy, có uy tín; khuyến khích sử dụng phần mềm có bản quyền, những phần mềm do Bộ GDĐT và Bộ TT&TT giới thiệu sử dụng miễn phí trong mùa dịch và kỹ năng phòng, tránh các nguy cơ, tình huống, tác hại có thể xảy ra trong quá trình dạy/học qua Internet.\r\n\r\nXây dựng và thực hiện quy chế quản lý, tổ chức dạy học qua Internet, trong đó hướng dẫn rõ quy trình quản lý, tổ chức một lớp học trực tuyến; các kỹ năng quản lý điều hành lớp học trực tuyến đối với giáo viên.\r\n\r\nNgoài ra, Sở GD&ĐT Hà Nội cũng nhấn mạnh đến trách nhiệm của người học khi tham gia lớp học trực tuyến, nhất là các hành vi không được làm đối với người học, không công bố công khai địa chỉ, mật khẩu lớp học, dùng các hệ thống dạy học trực tuyến có theo dõi được quá trình học tập, kiểm tra đánh giá học sinh... \r\n\r\nCông văn của Sở khẳng định cần tăng cường các biện pháp phối hợp giữa nhà trường và gia đình trong quản lý, tổ chức hoạt động dạy học qua Internet. Cùng với đó, các bậc phụ huynh nâng cao trách nhiệm, dành nhiều thời gian quan tâm, hỗ trợ học sinh kết nối, sử dụng phòng học trực tuyến an toàn và có biện pháp quản lý trong thời gian học sinh tham gia học trực tuyến và sử dụng Internet.\r\n\r\n Ngoài ra, Sở GD&ĐT cũng cho biết: Trong quá trình tổ chức dạy và học, nếu phát hiện những tình huống bất thường, giáo viên, học sinh, cha mẹ học sinh và cán bộ quản lý cần cung cấp thông tin kịp thời tới lãnh đạo nhà trường, cơ quan quản lý giáo dục, cơ quan công an để điều tra, xử lý theo quy định của pháp luật.\r\n\r\nHiện nay, nhiều địa phương trên cả nước đã thực hiện chương trình học trực tuyến qua Internet và truyền hình theo hướng dẫn của Bộ GD&ĐT. Trong quá trình tổ chức dạy học qua Internet thời gian vừa qua đã xảy ra hiện tượng bị kẻ xấu xâm nhập vào địa chỉ lớp học/phòng học trực tuyến, đăng tải nội dung xấu, độc, phản cảm, phản giáo dục... Đồng thới trong quá trình học qua Internet cũng đã có dấu hiệu lạm dụng, quấy rối và bắt nạt trẻ em, học sinh, sinh viên trên mạng, không đảm bảo an toàn và đã gây tâm lý hoang mang cho người học, người dạy, gây ảnh hưởng đến chất lượng dạy học qua Internet.\r\n\r\nĐể đảm bảo an toàn cho các lớp học trực tuyến, Bộ GD&ĐT đã phải gửi văn bản đến Sở GD&ĐT các địa phương, các cơ sở đào tạo trên toàn quốc nhằm siết chặt hơn nữa việc dạy và học.', '3_ictnews_hoc-truc-tuyen.jpg', '2020-03-17', NULL, NULL, NULL, NULL),
(4, 'Bí mật xHelper - phần mềm độc hại \'bất tử\' trên Android', 'Tin công nghệ (Nguồn: ICTNews)', 'Xuất hiện vào tháng 3/2019, xHelper đã lây nhiễm trên hơn 45.000 thiết bị Android. Loại malware này khiến các chuyên gia bảo mật phải đau đầu vì nó dường như “bất tử” trước các phần mềm diệt virus, có thể tự cài đặt lại khi bị gỡ bỏ hay reset (khôi phục cài đặt gốc) máy.\r\n', 'Các chuyên gia đã giải mã được cơ chế “không thể gỡ bỏ” ngay cả khi khôi phục cài đặt gốc của xHelper và tìm ra cách loại bỏ phần mềm độc hại này.\r\n\r\nXuất hiện vào tháng 3/2019, xHelper đã lây nhiễm trên hơn 45.000 thiết bị Android. Loại malware này khiến các chuyên gia bảo mật phải đau đầu vì nó dường như “bất tử” trước các phần mềm diệt virus, có thể tự cài đặt lại khi bị gỡ bỏ hay reset (khôi phục cài đặt gốc) máy.\r\n\r\nCho tới nay, xHelper chưa gây ra ảnh hưởng nghiêm trọng tới người dùng như đánh cắp thông tin, mà chỉ “khủng bố” bằng hàng loạt thông báo spam quảng cáo. Đa phần các nạn nhân ở Nga, Mỹ, Ấn Độ và Algeria. Chưa có bằng chứng xHelper từng được cung cấp trên Google Play.\r\n\r\nMới đây các chuyên gia Kaspersky Lab đã giải mã được bí mật về cơ chế của chương trình và tìm ra cách loại bỏ nó.\r\n\r\nĐầu tiên, malware này giả dạng làm một phần mềm dọn dẹp và tôi ưu tốc độ smartphone. Nhưng sau khi cài đặt, chương trình tự động biến mất không để lại dấu vết trên màn hình hay danh mục phần mềm. Điều này khiến người dùng khó tìm ra nó để gỡ bỏ. Cách duy nhất để tìm thấy là kiểm tra danh sách các ứng dụng đã cài đặt trong phần cài đặt hệ thống.\r\n\r\nSau khi cài đặt, xHelper tự thiết lập một backdoor (cửa sau) được điều khiển từ xa bởi các tin tặc. Sau đó, chương trình kích hoạt một lệnh khai thác Android và đoạt quyền quản trị trong hệ điều hành. Backdoor được tạo ra có quyền truy cập vào các dữ liệu nhạy cảm, bao gồm cookie trình duyệt được sử dụng để đăng nhập tự động vào các website.\r\n\r\nTheo Igor Golovin, một chuyên gia phân tích phần mềm độc hại của Kaspersky, việc tái nhiễm xHelper sau khi gỡ bỏ phần mềm hoặc reset máy là do một loại mã độc trojan có tên Triada. Triada chiếm quyền superuser (hay còn gọi là quyền Adminstrator của Windows) và cài đặt một loạt các tệp độc hại trực tiếp vào phân vùng hệ thống.\r\n\r\nCác tệp này được thiết lập ở chế độ chỉ đọc (read-only), ngụy trang giữa các tệp hệ thống được đăng ký thuộc tính bất biến, khiến chúng rất khó bị xóa vì hệ thống Android không cho phép gỡ bỏ các file dạng này. Tuy nhiên, cơ chế tự vệ này vẫn có thể bị xóa bởi lệnh chattr. Đây là loại lệnh dùng để khóa tệp về chế độ read-only nên có thể sử dụng chính nó để mở chế độ write (ghi) rồi xóa.\r\n\r\nThậm chí xHelper xóa tất cả các ứng dụng liên quan đến root (ví dụ như Superuser) và không cho phép người dùng gỡ bỏ ngay cả khi có quyền. Chưa hết, nó tự sửa đổi các thư viện Android để ngăn việc cài lại phân vùng trong chế độ Ghi hệ thống.\r\n\r\nVậy làm thế nào để loại bỏ xHelper?\r\n\r\nNhư đã nói ở trên, thao tác gỡ bỏ thông thường sẽ không thể loại bỏ được hoàn toàn các tệp ẩn của xHelper trên hệ thống. Chương trình com.diag.patches.vm8u được cài đặt trong phân vùng hệ thống sẽ giúp xHelper “hồi sinh” ngay khi có cơ hội.\r\n\r\nNhưng nếu điện thoại có Recovery Mode (Chế độ Khôi phục), người dùng có thể thử trích xuất tệp libc.so từ chương trình cơ sở gốc (original firmware) và thay thế tệp bị nhiễm độc bằng nó trước khi xóa tất cả các phần mềm độc hại khỏi phân vùng hệ thống.\r\n\r\nĐiều đáng chú ý là phần mềm độc hại này chủ yếu lây nhiễm với các phiên bản Android cũ như 6 và 7, trên một số loại “smartphone fake” của Trung Quốc. Một cách khác để loại bỏ vĩnh viễn xHelper khỏi máy dễ dàng hơn là cài đặt lại, nâng cấp máy bằng một phiên bản hệ điều hành chuẩn tải từ nhà cung cấp hoặc flash ROM với một bản tương thích.\r\n\r\nTheo Zing', '4_znews-photo-zadn-vn_malware_2.jpg', '2020-04-02', NULL, NULL, NULL, NULL),
(5, 'TSMC tiết lộ chíp tiến trình mới nhất, giảm tiêu thụ năng lượng 30%', 'Tin công nghệ (Nguồn: ICTNews)', 'Mới đây, Công ty sản xuất và chế tạo chất bán dẫn Đài Loan TSMC đã chính thức tiết lộ chi tiết về tiến trình 3nm mới nhất, giúp tăng hiệu suất tối đa 15% và mức tiêu thụ năng lượng giảm 30%.', 'Mới đây, Công ty sản xuất và chế tạo chất bán dẫn Đài Loan TSMC đã chính thức tiết lộ chi tiết về tiến trình 3nm mới nhất, giúp tăng hiệu suất tối đa 15% và mức tiêu thụ năng lượng giảm 30%.\r\n\r\nTiến trình mới nhất của TSMC có mật độ bóng bán dẫn đạt 250 triệu/mm². Đây sẽ là một kỷ lục mới về mật độ bóng bán dẫn trên một đơn vị diện tích. \r\n\r\nĐể tham khảo, ta có thể lấy con chíp Kirin 990 5G với tiến trình 7nm sử dụng các thiết bị khắc bằng tia siêu cực tím (EUV: Extreme Ultraviolet) của TSMC cho thấy nó có kích thước 113,31mm², mật độ bóng bán dẫn 10,3 tỷ, trung bình 90 triệu bóng bán dẫn/mm². Như vậy, mật độ bóng bán dẫn với tiến trình 3nm gấp hơn 2,7 lần so với tiến trình 7nm. \r\n\r\nVề mặt cải thiện hiệu suất, hiệu suất của tiến trình 5nm của TSMC cao hơn 10% - 15% so với tiến trình 7nm và mức tiêu thụ năng lượng giảm 25% - 30%. Tuy nhiên, hiệu suất của tiến trình 3nm cao hơn 5% so với tiến trình 5nm và mức tiêu thụ năng lượng tăng lên 15%.\r\n\r\nNgoài ra, TSMC cho biết việc phát triển tiến trình 3nm phù hợp với lịch trình ban đầu của họ. Theo Giám đốc điều hành của TSMC, CC Wei thì tiến trình 3nm sẽ bắt đầu sản xuất thử nghiệm vào năm 2021 và sẽ đưa vào sản xuất hàng loạt vào nửa cuối năm 2022.\r\n\r\nVề công nghệ, TSMC đã thực hiện đánh giá các tùy chọn khác nhau và tin rằng quy trình dựa trên công nghệ transitor hiệu ứng trường vây (FinFET) hiện tại hiệu quả hơn về chi phí và mức tiêu thụ năng lượng. Do đó, bộ chip với tiến trình 3nm đầu tiên vẫn sẽ sử dụng công nghệ bóng bán dẫn FinFET. \r\n\r\nTuy nhiên, Samsung, một đối thủ của TSMC về lĩnh vực sản xuất bán dẫn đang đặt cược vào tiến trình 3nm để dành lấy lợi thế vì vậy họ đang tận dụng tối đa sự tiến bộ về công nghệ bằng cách sẽ loại bỏ các bóng bán dẫn FinFET và trực tiếp sử dụng công nghệ cổng bao quanh (GAA: Gate –all-ground) trong quá trình sản xuất chíp của họ.\r\n\r\nPhan Văn Hoà', '5_tsmc-pesquisa-google.png', '2020-03-30', NULL, NULL, NULL, NULL),
(6, 'Ứng dụng Bluezone cảnh báo sớm Covid-19 không theo dõi người dùng', 'Tin công nghệ (Nguồn: ICTNews)', 'Nguyên tắc của Bluezone là bảo mật, ẩn danh và minh bạch. Ứng dụng chỉ lưu dữ liệu trên điện thoại của người dùng, không chuyển lên hệ thống cũng như không thu thập vị trí của người dùng. Ứng dụng cảnh báo sớm khi người từng tiếp xúc nhiễm Covid-19.', 'Nguyên tắc của Bluezone là bảo mật, ẩn danh và minh bạch. Ứng dụng chỉ lưu dữ liệu trên điện thoại của người dùng, không chuyển lên hệ thống cũng như không thu thập vị trí của người dùng. Ứng dụng cảnh báo sớm khi người từng tiếp xúc nhiễm Covid-19.\r\n\r\nBộ Thông tin và Truyền thông (Bộ TT&TT) và Bộ Y tế mới đây đã ra mắt ứng dụng Bluezone. Ứng dụng này có khả năng cảnh báo sớm cho người dùng khi những người họ từng tiếp xúc bị xác định nhiễm Covid-19. Bluezone là một trong những giải pháp được Việt Nam phát triển nhằm đưa cuộc sống thời đại dịch Covid-19 sớm trở lại như bình thuờng.\r\n\r\nTheo ông Nguyễn Tử Quảng - đơn vị phát triển Bluezone, người dùng cần tham gia vào cộng đồng Bluezone bằng cách cài đặt ứng dụng. \r\n\r\nTrong quá trình sinh hoạt hàng ngày, khi mọi người có tiếp xúc, ứng dụng trên điện thoại của họ sẽ tự “nói chuyện\" với nhau. Nếu phát hiện có sự tiếp xúc gần (trong khoảng cách 2m), thiết bị sẽ tự ghi nhận vào nhật ký.\r\n\r\nNếu phát hiện ra một người nhiễm bệnh Covid-19 (F0), dữ liệu của người nhiễm bệnh đó sẽ được nhập lên trên hệ thống, từ đó chuyển xuống tất cả các thiết bị đang sử dụng. \r\n\r\nỨng dụng Bluezone trên máy sẽ so sánh dữ liệu của F0 với lịch sử tiếp xúc được ghi nhận từ trước. Nếu phát hiện thiết bị đã từng tiếp xúc với F0 trong thời gian đủ lớn, hệ thống sẽ báo cho người sử dụng về nguy cơ có thể lây nhiễm bệnh (F1). \r\n\r\n“Bluezone thậm chí có thể cung cấp cụ thể việc bạn đã tiếp xúc với F0 vào lúc nào và trong khoảng thời gian là bao nhiêu lâu. Ứng dụng cũng sẽ cung cấp liên hệ của cơ quan y tế có thẩm quyền và khuyến cáo người sử dụng liên hệ để được hướng dẫn, trợ giúp”, ông Nguyễn Tử Quảng cho biết. \r\n\r\nKhi cơ quan thẩm quyền nhận được dữ liệu của F1, họ sẽ tiếp tục nhập lên hệ thống để cảnh báo các F tiếp theo một cách nhanh chóng và chính xác.  \r\n\r\nẨn danh bằng ID, không lưu vị trí người dùng\r\n\r\nChia sẻ về Bluezone, ông Nguyễn Tử Quảng cho biết, triết lý của ứng dụng này để người dùng tự bảo vệ chính mình rồi sau đó mới đến bảo vệ cộng đồng. \r\n\r\n“Do được cảnh báo sớm, người sử dụng Bluezone sẽ biết mình mình có thuộc diện F2, F3, F4,... hay không mỗi khi có F0 (người nhiễm Covid-19) mới. Khi người dùng kết nối với hệ thống, mạng lưới này mới đủ lớn để từ đó bảo vệ cho cộng đồng”, ông Quảng nói. \r\n\r\nKhi nói về tính riêng tư của Bluezone, vị chuyên gia về bảo mật này khẳng định, ứng dụng Bluezone chỉ lưu dữ liệu trên máy người dùng, tuyệt đối không đưa lên server. \r\n\r\nTheo đó, Bluezone cũng sẽ không thu thập vị trí. Ứng dụng này chỉ ghi nhận 2 người có gặp nhau lúc nào đó và trong bao lâu, chứ không biết ở chỗ nào. Người dùng Bluezone sẽ ẩn danh do sử dụng ID mà hệ thống tự sinh ra. \r\n\r\nChỉ khi người dùng bị xác định là F0 thì dữ liệu mới được nhập lên hệ thống rồi đưa xuống dưới. Khi đó, người dùng được báo là F1 sẽ tự đưa dữ liệu lên hệ thống để bảo vệ cộng đồng. \r\n\r\nĐể minh bạch công việc mình làm, đơn vị phát triển cho biết sẽ cung cấp bộ mã nguồn mở trên trang web bluezone.vn. Ngoài ra, bộ mã nguồn mở của Bluezone cũng sẽ có tại địa chỉ quốc tế là Bluezone.ai, ông Quảng nói.  \r\n\r\nTrọng Đạt', '6_bluezone2_800x450.jpg', '2020-04-03', NULL, NULL, NULL, NULL),
(7, 'Vingroup nghiên cứu thành công công nghệ nhận diện khuôn mặt cả khi dùng khẩu trang', 'Tin công nghệ (Nguồn: ICTNews)', 'VinAI sử dụng mô hình học sâu (deep learning) mới nhất và đặc thù do các chuyên gia của Viện nghiên cứu và tối ưu hóa để nghiên cứu công nghệ nhận diện ổn định cả khi sử dụng và không sử dụng khẩu trang. Mô hình deep learning với thiết kế mô phỏng cách hoạt động của mạng lưới thần kinh trong não người để huấn luyện và tự động trích xuất các thông tin có giá trị trên một phần khuôn mặt, nhằm nhận diện ngay cả khi người dùng sử dụng khẩu trang.', 'Ngày 20/4, Viện Nghiên cứu Trí tuệ Nhân tạo VinAI Research (thuộc Vingroup) công bố đã nghiên cứu thành công công nghệ nhận diện khuôn mặt chính xác và ổn định cả khi sử dụng khẩu trang. Đây cũng là top những đơn vị đầu tiên trên thế giới nghiên cứu thành công công nghệ này.\r\n\r\nVinAI sử dụng mô hình học sâu (deep learning) mới nhất và đặc thù do các chuyên gia của Viện nghiên cứu và tối ưu hóa để nghiên cứu công nghệ nhận diện ổn định cả khi sử dụng và không sử dụng khẩu trang. Mô hình deep learning với thiết kế mô phỏng cách hoạt động của mạng lưới thần kinh trong não người để huấn luyện và tự động trích xuất các thông tin có giá trị trên một phần khuôn mặt, nhằm nhận diện ngay cả khi người dùng sử dụng khẩu trang.\r\n\r\nKết quả nghiên cứu cho thấy khi giả lập đeo khẩu trang - hệ thống nhận diện của VinAI đạt được độ chính xác tốt hơn đáng kể so với công nghệ hiện tại trên thế giới. Điểm ưu việt của công nghệ nhận diện do VinAI phát triển là thuật toán và cách sử dụng công nghệ nhận diện dẫn đến hiệu quả ổn định, có độ chính xác cao.\r\n\r\nĐặc biệt, hệ thống nhận diện do VinAI phát triển đơn giản, chỉ sử dụng thông tin từ các camera thường và không cần đến các cảm biến phụ (như hồng ngoại hoặc cảm biến chiều sâu). Đây là yếu tố quan trọng giúp giá thành sản phẩm không cao, có thể dễ dàng tích hợp vào các hệ thống camera đã có sẵn. Sản phẩm có thể áp dụng rộng rãi vào các hệ thống chấm công, hệ thống xác thực cho tổ chức và doanh nghiệp.\r\n\r\n“Khẩu trang là vật dụng thiết yếu trong quy tắc phòng chống dịch bệnh nhưng khi người dùng đeo khẩu trang, độ chính xác của công nghệ nhận diện mặt có thể giảm hơn 50%. Vì thế, chúng tôi đã cấp bách phát triển công nghệ này nhằm đáp ứng kịp thời nhu cầu xã hội”, Tiến sĩ Bùi Hải Hưng, Viện trưởng Viện Nghiên cứu Trí tuệ Nhân tạo VinAI Research) chia sẻ.\r\n\r\nBên cạnh kết quả trên, VinAI hiện cũng đã hoàn thành giải pháp tự động giám sát đeo khẩu trang trên camera. Giải pháp này có thể hỗ trợ tích cực trong việc quản lý giãn cách, ứng dụng khi các tổ chức, doanh nghiệp quay trở lại hoạt động, sản xuất sau mùa dịch.\r\n\r\nHiện tại VinAI Research đang phối hợp với Công ty sản xuất và thiết điện tử VinSmart triển khai công nghệ nhận diện trên điện thoại Vsmart, cho phép người sử dụng mở khoá bằng khuôn mặt một cách thuận tiện. Việc xúc tiến ứng dụng trên Vsmart cũng đưa VinAI trở thành một trong những đơn vị đầu tiên trên thế giới ứng dụng công nghệ nhận diện không cần bỏ khẩu trang lên các sản phẩm thương mại.\r\n\r\nSong song với việc thương mại hóa, VinAI sẵn sàng cung cấp công nghệ nhận diện mới nhất này cho các đối tác vì mục đích phục vụ y tế cộng đồng, trong bối cảnh dịch bệnh tiếp tục diễn biến phức tạp.', '7_vingroup_vsmart.jpg', '2020-04-08', NULL, NULL, NULL, NULL),
(8, '10 cách công nghệ 5G có thể thay đổi môi trường', 'Tin công nghệ (Nguồn: ICTNews)', 'Công nghệ 5G cho phép mọi thiết bị sử dụng năng lượng được kết nối có thể cảm nhận và phản hồi một cách thông minh theo yêu cầu hoặc khi có các thay đổi khác, điều này có ý nghĩa sâu sắc đối với việc sử dụng năng lượng.', 'Công nghệ 5G cho phép mọi thiết bị sử dụng năng lượng được kết nối có thể cảm nhận và phản hồi một cách thông minh theo yêu cầu hoặc khi có các thay đổi khác, điều này có ý nghĩa sâu sắc đối với việc sử dụng năng lượng.\r\n\r\nTrong bài viết này, chúng ta sẽ xem xét một số yếu tố tác động đến sự biến đổi môi trường mà công nghệ 5G sẽ mang lại. Từ phương tiện thông minh hơn, chất lượng không khí tốt lên, tiêu thụ năng lượng hiệu quả hơn, mạng 5G sẽ góp phần quan trọng trong việc cải thiện môi trường của chúng ta.\r\n\r\n1. Lưới điện thông minh\r\nCác lưới điện thông minh của Vương quốc Anh đã sử dụng các công nghệ đo lường, truyền thông và điều khiển để quản lý sử dụng năng lượng nhưng mạng 5G sẽ đưa việc quản lý tài nguyên lên một cấp độ khác.\r\n\r\nCác thiết bị vạn vật kết nối (IoT) như trong xe hơi thông minh, hộ gia đình, tòa nhà và sân vận động sẽ nhanh chóng đưa ra cảnh báo về lưới điện đang sử dụng để có những điều chỉnh và đáp ứng một cách linh hoạt. Các thiết bị IoT ngừng hoạt động khi không dùng tới sẽ làm giảm đáng kể nhu cầu năng lượng. Trên thực tế, một báo cáo gần đây của nhà mạng O2 lập luận rằng điều này có thể giúp giảm 12% năng lượng dùng trong hộ gia đình, tương đương 6,4 triệu tấn CO2.\r\n\r\nViệc sử dụng các nguồn năng lượng tái tạo như gió và mặt trời đang gia tăng nhưng có thể bị gián đoạn. Vì vậy, công nghệ 5G sẽ cho phép quản lý tốt hơn các tài nguyên này để cân bằng trong việc tạo ra và tiêu thụ năng lượng.\r\n\r\n2. Thành phố thông minh\r\nThành phố thông minh nhằm giải quyết nhiều vấn đề ảnh hưởng đến môi trường đô thị và tác động của môi trường đối với cuộc sống đô thị là một trong số đó. Các cảm biến được kết nối với xe điện, đèn giao thông, đồng hồ thông minh, thùng rác, vườn trong đô thị và nhiều thứ khác sẽ gửi lại thông tin cho các nhà quy hoạch thiết kế thành phố của họ để sử dụng ít năng lượng hơn và giảm lượng khí thải carbon.\r\n\r\nCác thiết bị và cảm biến trên đường, ô tô, đèn đường, hộ gia đình, tòa nhà và nhiều thứ khác có thể cung cấp dữ liệu cho phép chính quyền địa phương sử dụng các hệ thống trí tuệ nhân tạo (AI) để điều chỉnh mức tiêu thụ năng lượng. Các chính sách năng lượng thông minh được thực hiện trên toàn thành phố để giảm sử dụng năng lượng, giám sát và quản lý chất lượng không khí, cũng như theo dõi điện, nước và chất thải. Theo báo cáo của công ty McKinsey, đến năm 2025, các thành phố thông minh sẽ giảm lượng khí thải nhà kính từ 10 - 15% và tiết kiệm từ 25-80 lít nước mỗi người mỗi ngày.\r\n\r\n3. Đèn đường thông minh\r\nĐèn đường được kết nối là một yếu tố quan trọng của bất kỳ thành phố thông minh nào, nó có thể giúp bảo vệ môi trường và tiết kiệm chi phí điện năng. Rõ ràng nhất là khi kết hợp với dữ liệu lớn (big data), các yêu cầu có thể được phân tích để đảm bảo rằng đèn chỉ sáng khi có nhu cầu từ người dân hoặc phương tiện giao thông. Tương tự, đèn đường sẽ kết nối với con người để đảm bảo lối đi của người qua đường được chiếu sáng và sẽ tắt khi không có người qua lại.\r\n\r\nMột báo cáo của nhà mạng O2 năm 2018 cho thấy các cảm biến 5G và công nghệ vô tuyến có thể tiết kiệm cho thành phố trung bình 1,3 triệu bảng tiền điện thông qua việc áp dụng đèn đường LED thông minh. Đèn đường cũng có thể được cố định bằng các cảm biến để theo dõi ô nhiễm không khí hoặc nhiệt độ.\r\n\r\n4. Quản lý nước\r\nViệc rò rỉ và vỡ đường ống nước sẽ làm một lượng nước khổng lồ bị lãng phí trên toàn cầu. Trên thực tế, một báo cáo năm 2019 từ nhóm nghiên cứu Brookings của Mỹ ước tính lượng nước thất thoát từ ​​các đường ống bị vỡ, bị rò rỉ và trộm cắp nước chiếm tới 50% tổng số nguồn cung toàn cầu hoặc 75% tại các thị trường mới nổi.\r\n\r\nCác cảm biến được kết nối IoT trong đường ống hoặc trên mặt đất sẽ cảnh báo cho các công ty cấp nước về thiệt hại cho đường ống hoặc rò rỉ trước khi mất quá nhiều nước. Tương tự, đồng hồ nước thông minh 5G trong các hộ gia đình có thể được thiết lập để hạn chế sử dụng. Việc tưới tiêu trong các trang trại tốn nhiều nước và nước được dùng mà không cần quan tâm nhiều đến độ ẩm của đất. Cảm biến 5G sẽ được sử dụng để đảm bảo nước không bị lãng phí trên các loại cây trồng không cần đến nó.\r\n\r\n5. Xe điện thông minh\r\nNhững chiếc xe tự lái được điều khiển bằng kết nối 5G khi được kết hợp với một mạng có khả năng hỗ trợ hàng triệu thiết bị được kết nối, hứa hẹn hiệu quả hơn đáng kể so với các phương tiện hiện tại.\r\n\r\nDẫn đường dựa trên 5G sẽ cho phép phương tiện di chuyển nhanh hơn và tránh được các tuyến đường bị tắc. Người vận hành bảo trì các tuyến đường sẽ nhận được cảnh báo về những nơi bề mặt đường bị hư hỏng thông qua các cảm biến trong xe hơi hoặc thông qua cơ sở hạ tầng xung quanh, điều này sẽ giúp cải thiện hiệu quả xử lý.\r\n\r\n6. Sản xuất và tiêu thụ thực phẩm\r\nNhư đã đề cập, các cảm biến 5G trong đất có thể theo dõi độ ẩm, nghĩa là ít lãng phí nước cho cây trồng nhưng đồng thời sẽ đánh giá liệu cây trồng đã nhận đủ hay quá nhiều ánh sáng mặt trời. Chúng sẽ có thể kiểm tra sự cân bằng các chất dinh dưỡng trong đất và liệu cây có bị héo hay không để đưa ra các biện pháp nhằm ngăn chúng bị hư hỏng.\r\n\r\nSản phẩm mua về có thể bị hư hỏng trong quá trình bảo quản, vì vậy các cảm biến này sẽ giúp cho người dùng nhận được cảnh báo từ tủ lạnh để dùng trước ngày hết hạn nếu chúng được kết nối với IoT. Điều này sẽ ngăn chặn lãng phí thực phẩm. Theo báo cáo của Tổ chức Lương thực và Nông lương Liên Hợp Quốc, khoảng 1/3 thực phẩm được sản xuất cho tiêu dùng của chúng ta bị mất hoặc lãng phí, trong đó hơn 40% là trái cây và rau quả.\r\n\r\n7. Chất lượng không khí\r\nLiên Hợp Quốc lập luận rằng vào năm 2050, 68% dân số sẽ sống ở các khu vực đô thị dày đặc dẫn đến nhiều chất thải, tắc nghẽn và ô nhiễm. Với sự trợ giúp của các cảm biến nằm rải rác trong cơ sở hạ tầng thành phố thông minh (như gắn trên đèn đường, tòa nhà hoặc biển báo đường bộ), các nhà quản lý đô thị có thể xây dựng một hệ thống quản lý chất lượng không khí bằng cách sử dụng dữ liệu chính xác theo thời gian thực.\r\n\r\nĐiều này sẽ được dùng để quản lý các điểm nóng ô nhiễm. Hệ thống lọc không khí, có khả năng được lắp đặt trong các tòa nhà thông minh; phương tiện và thiết bị tiêu dùng cũng có thể được kết nối với các cảm biến 5G nhằm phát hiện các hạt bụi trong không khí.\r\n\r\n8. Quản lý hiệu quả năng lượng trong các tòa nhà\r\nPhần lớn năng lượng của thế giới được sử dụng bởi các tòa nhà và các thiết kế kết hợp cảm biến 5G có thể giảm đáng kể việc sử dụng năng lượng. Hệ thống ánh sáng, sưởi ấm, làm mát và các hoạt động khác được phân bổ theo nhu cầu, với năng lượng do chính tòa nhà tạo ra, chẳng hạn thông qua hệ thống năng lượng mặt trời.\r\n\r\nTrong các văn phòng, 5G có thể được kết hợp với dữ liệu lớn và AI để tạo ra các mô hình từ bước chân của công nhân để xác định khi nào nên điều chỉnh ánh sáng, sưởi ấm hoặc điều hòa không khí. Các tòa nhà sử dụng nhiều tài nguyên như bệnh viện cũng có thể sử dụng các cảm biến 5G và AI để giúp thực hiện các bảo dưỡng quan trọng dựa trên các hình mẫu trước đó. Điều này cũng hữu ích trong một trường đại học hoặc khuôn viên công ty rộng lớn, nơi có nhiều tòa nhà đa năng.\r\n\r\n9. Đồng hồ điện thông minh\r\nCơ sở hạ tầng công tơ điện được kết nối 5G sẽ giảm sự cố mất điện và tăng đột biến điện và giúp người dùng giảm hóa đơn năng lượng hàng tháng, cung cấp quyền truy cập vào các thiết bị như công tơ điện, khí đốt và nước thông qua các thiết bị di động từ xa.\r\n\r\nHệ thống quản lý năng lượng gia đình được kết nối với đồng hồ thông minh sẽ cho phép các hộ gia đình sửa đổi việc sử dụng tiện ích của họ. Ủy ban châu Âu ước tính rằng khoảng 200 triệu đồng hồ điện thông minh và 45 triệu đồng hồ đo khí đốt sẽ được triển khai vào năm 2020, phục vụ cho 72% khách hàng điện và 40% khách hàng sử dụng khí đốt. \r\n\r\n10. Cơ sở hạ tầng 5G\r\nĐây là một vấn đề gây tranh cãi vì cơ sở hạ tầng 5G sẽ cần nhiều thiết bị hơn, nhiều trạm thu-phát tế bào nhỏ hơn và nhiều trung tâm dữ liệu hơn, tất cả đều có nghĩa là tăng sử dụng tài nguyên và tiêu thụ năng lượng.\r\n\r\nMột số báo cáo ước tính rằng phần trăm phát thải trên toàn thế giới của ngành CNTT có thể tăng từ 3% hiện nay lên 14% vào năm 2040, đây là kết quả không mong muốn. Tuy nhiên, hầu hết các công ty cung cấp thiết bị mạng đều đang thiết kế và sản xuất các thiết bị có hiệu quả năng lượng cao.\r\n\r\nHuawei đã xây dựng một số trạm gốc 5G tại Trung Quốc sử dụng phần mềm quản lý năng lượng và yêu cầu ít linh kiện điện tử tạo nhiệt hơn, những thiết bị này sử dụng điện ít hơn 20% so với trạm 4G. Tương tự, 5G có thể quản lý năng lượng mặt trời hoặc làm mát bằng chất lỏng trong một trạm gốc theo nhu cầu và một số công ty châu Âu đã giảm tới 80% lượng khí thải CO2 theo cách này. 5G sẽ sử dụng các chế độ ngừng hoạt động trong thời gian không có lưu lượng phát sinh, sử dụng các thuật toán phân tích và AI để mô hình hóa việc sử dụng năng lượng. \r\n\r\nPhan Văn Hòa (Theo 5G Radar)', '8_smart_city_header_2.jpg', '2020-03-23', NULL, NULL, NULL, NULL),
(9, 'Ứng phó với Covid-19, FPT họp cổ đông online theo tác phong “thời chiến”', 'Tin công nghệ (Nguồn: ICTNews)', 'Ngày 8/4, trong bối cảnh diễn biến phức tạp của dịch Covid-19, FPT là công ty VN30 đầu tiên đã tổ chức thành công Đại hội đồng Cổ đông thường niên FPT năm 2020 với cả hai hình thức trực tiếp và trực tuyến nhờ ứng dụng công nghệ trí tuệ nhân tạo và hệ thống hội nghị trực tuyến.', 'Ngày 8/4, trong bối cảnh diễn biến phức tạp của dịch Covid-19, FPT là công ty VN30 đầu tiên đã tổ chức thành công Đại hội đồng Cổ đông thường niên FPT năm 2020 với cả hai hình thức trực tiếp và trực tuyến nhờ ứng dụng công nghệ trí tuệ nhân tạo và hệ thống hội nghị trực tuyến.\r\n\r\nTheo đó, FPT đã ứng dụng công nghệ trí tuệ nhân tạo để xác thực và định danh cổ đông hoàn toàn tự động. Các cổ đông được xác thực danh tính theo 3 bước là xác thực thông tin, thông tin cá nhân, xác thực khuôn mặt và xác thực email. Hệ thống mất khoảng 2 giây thực hiện quy trình định danh trực tuyến bao gồm: tự động nhận diện hình ảnh, phân tích, trích xuất, xác thực thông tin và gửi phản hồi cho cổ đông.\r\n\r\nQuy trình định danh được phát triển dựa trên nền tảng Trí tuệ nhân tạo FPT.AI trong lĩnh vực Nhận dạng ảnh - FPT.AI Vision. Những ứng dụng này đang được FPT triển khai tại nhiều tổ chức tài chính trong và ngoài nước, mang lại hiệu quả cao cho hoạt động vận hành như Ngân hàng Quân đội MB, Ngân hàng Quốc dân NCB... \r\n\r\nPhát biểu tại Đại hội đồng cổ đông, ông Nguyễn Hữu Điền, đại diện Dragon Capital cho biết: “Đây là một trong những Đại hội đồng cổ đông đầu tiên được tổ chức trực tuyến đã thể hiện đúng tinh thần của FPT - “chống dịch như chống giặc”, vẫn không dừng việc kinh doanh. Đại hội đồng cổ đông FPT sẽ tạo ra một cách tiếp cận mới cho các Đại hội đồng cổ đông sắp tới. Không cần chờ khi dịch kết thúc mà có thể ứng dụng giải pháp phần mềm, công nghệ mới để họp trực tuyến. Giai đoạn này có nguy nhưng như ông Trương Gia Bình nói, cơ hội cũng lớn”.\r\n\r\nĐây là động thái của FPT nhằm đảm bảo quyền lợi và sự an toàn của các cổ đông nhờ thế mạnh công nghệ, đồng thời thể hiện tinh thần quyết tâm vượt qua mọi thách thức trong “thời chiến”. Năm 2019, FPT trả cổ tức với tỷ lệ 35%, trong đó 20% bằng tiền mặt và 15% bằng cổ phiếu và dự kiến trả cổ tức 2020 bằng tiền mặt với tỷ lệ 20%.\r\n\r\nĐại hội đồng cổ đông thường niên FPT đã thông qua kế hoạch kinh doanh năm 2020 với doanh thu và lợi nhuận dự kiến tăng lần lượt là 17% và 18% so với năm 2019 như đã đưa ra đầu tháng 1 và uỷ quyền cho HĐQT thực hiện các kịch bản kinh doanh khác nhau tuỳ theo diễn biễn thực tế. Nhằm đảm bảo hoạt động kinh doanh diễn ra liên tục, bảo vệ tối đa lợi ích cho khách hàng, cổ đông và các bên liên quan, FPT sẽ tập trung vào 3 hướng chính là thực hiện các biện pháp tài chính để tối ưu chi phí, đầu tư và dòng tiền. Thứ hai là đầu tư, triển khai các dự án chuyển đổi số nội bộ để tăng cường năng lực cạnh tranh. Thứ ba là đảm bảo kinh doanh liên tục thông qua chuyển mô hình làm việc online, làm việc từ nhà, triển khai các phương án đảm bảo an toàn cho con người cũng như an toàn sản xuất kinh doanh.\r\n\r\nÔng Trương Gia Bình, Chủ tịch FPT cho biết: “Dịch bệnh Covid là cú hích thúc đẩy tiến trình của cách mạng 4.0. Đây là một cuộc chiến mới, thử thách mới. Trong nguy có cơ và FPT đã sẵn sàng đương đầu để vượt qua thách thức này. FPT sẽ chuyển sang chế độ thời chiến với tinh thần làm việc bằng hai. Mỗi người FPT là một chiến sĩ, mỗi đơn vị là một pháo đài hoạt động mạnh mẽ trên 3 mặt trận: vừa chống dịch, vừa đảm bảo các hoạt động kinh doanh để bảo vệ công ăn việc làm, vừa tìm kiếm các cơ hội kinh doanh mới ở cả trong và ngoài nước. Việc ứng dụng những thành tựu công nghệ để tổ chức Đại hội đồng cổ đông trong bối cảnh dịch bệnh là một minh chứng cho nỗ lực sống chung với lũ, đảm bảo mọi công việc diễn ra bình thường của FPT. Ngoài ra, FPT sẽ tập trung củng cố nội lực, liên kết sức mạnh của các công ty trong tập đoàn, chuẩn bị thế và lực để vươn lên mạnh mẽ hơn sau dịch”. \r\n\r\nNgay trong dịch bệnh, FPT đã tìm kiếm cơ hội mới và triển khai các cách thức sáng tạo để mang về hợp đồng chuyển đổi số giá trị từ thị trường toàn cầu. FPT vừa trở thành nhà cung cấp dịch vụ cho 1 hãng ô tô lớn nhất nước Mỹ, ký nhiều hợp đồng với các công ty sản xuất thiết bị y tế tại Nhật, Mỹ, Italia và phụ trách chuyển đổi số cho hãng sản xuất xe máy lớn nhất Việt Nam.\r\n\r\nTheo sự báo của IDC, năm 2021 sẽ là năm của dịch vụ đám mây khi đại dịch Covid-19 cho thấy nhu cầu quan trọng đối với sự linh hoạt trong kinh doanh. Đến năm 2022, hơn 90% doanh nghiệp trên toàn thế giới sẽ dựa vào nền tảng đám mây để có một cơ sở hạ tầng CNTT mạnh, có thể mở rộng và tối ưu hiệu quả về chi phí. Nhu cầu chuyển đổi số những năm gần đây đã tăng trưởng nhanh và sẽ ngày càng cao sau khi kết thúc dịch bệnh, và đây là cơ hội tốt cho FPT.\r\n\r\nVới thế mạnh của Tập đoàn tiên phong chuyển đổi số cũng như kết quả tích cực trong 2019, FPT xác định Chuyển đổi số sẽ tiếp tục tạo ra cho FPT những bước phát triển đột phá về cả vị thế, năng lực và quy mô tăng trưởng, nâng cao giá trị cạnh tranh trên toàn cầu. Công ty sẽ tiếp tục theo đuổi mục tiêu Top 50 Công ty cung cấp dịch vụ chuyển đổi số hàng đầu thế giới vào năm 2030, tăng 40 -50% doanh thu chuyển đổi số trong giai đoạn 2020 - 2022.\r\n\r\nNăm 2019, FPT ghi nhận doanh thu hợp nhất đạt 27.717 tỷ đồng, tăng 19,4% so với năm 2018; lợi nhuận trước thuế đạt 4.665 tỷ đồng, tăng 20,9% so với năm 2018. Doanh thu và lợi nhuận từ thị trường nước ngoài tiếp tục tăng trưởng cao, đạt tương ứng 11.452 tỷ đồng, tăng 25,7% và 1.896 tỷ đồng, tăng 27% so với cùng kỳ. Tỷ suất lợi nhuận của Tập đoàn FPT tiếp tục được cải thiện, đạt 16,8%.', '9_man-hinh-hien-thi-hon-400-nguoi-dang-tham-gia-online-dhcd-fpt-2020-1-.jpg', '2020-04-07', NULL, NULL, NULL, NULL),
(10, 'abcd', 'Tin công nghệ (Nguồn: ICTNews)', 'Tr&ecirc;n blog đăng h&ocirc;m 8/4, Microsoft cho biết &ldquo;mọi quốc gia tr&ecirc;n thế giới đều chứng kiến &iacute;t nhất một vụ tấn c&ocirc;ng c&oacute; chủ đề Covid-19&rdquo;. C&aacute;c đối tượng xấu thiết kế lại email lừa đảo (phishing) v&agrave; m&atilde; độc kh&aacute;c để nhắc tới căn bệnh n&agrave;y. C&aacute;c nước đang trải qua dịch bệnh - nơi mọi người cần th&ocirc;ng tin y tế nhất - dễ bị tổn thương nhất.', 'Theo nghi&ecirc;n cứu mới từ Microsoft, hacker đ&atilde; triển khai c&aacute;c cuộc tấn c&ocirc;ng c&oacute; chủ đề Covid-19 tại 241 quốc gia v&agrave; v&ugrave;ng l&atilde;nh thổ. Tr&ecirc;n blog đăng h&ocirc;m 8/4, Microsoft cho biết &ldquo;mọi quốc gia tr&ecirc;n thế giới đều chứng kiến &iacute;t nhất một vụ tấn c&ocirc;ng c&oacute; chủ đề Covid-19&rdquo;. C&aacute;c đối tượng xấu thiết kế lại email lừa đảo (phishing) v&agrave; m&atilde; độc kh&aacute;c để nhắc tới căn bệnh n&agrave;y. C&aacute;c nước đang trải qua dịch bệnh - nơi mọi người cần th&ocirc;ng tin y tế nhất - dễ bị tổn thương nhất. Theo &ocirc;ng Rob Lefferts, Ph&oacute; Chủ tịch Microsoft 365 Security, c&aacute;c cuộc tấn c&ocirc;ng theo s&aacute;t diễn biến dịch bệnh tr&ecirc;n to&agrave;n cầu. Những nước c&oacute; số người nhiễm Covid-19 cao nhất đồng thời l&agrave; c&aacute;c nước bị ảnh hưởng nhiều nhất từ tấn c&ocirc;ng lợi dụng Covid-19. Sự bối rối, lo lắng, nỗi sợ h&atilde;i khiến họ bấm v&agrave;o những đường link v&agrave; đ&oacute; l&agrave; điều m&agrave; hacker mong muốn. Bộ c&ocirc;ng cụ an ninh mạng Microsfot Threat Protection ph&aacute;t hiện gần 60.000 email chứa c&aacute;c tập tin đ&iacute;nh k&egrave;m hoặc URL độc hại li&ecirc;n quan tới Covid-19 được gửi đi mỗi ng&agrave;y. Tuy nhi&ecirc;n, &ldquo;tin mừng&rdquo; l&agrave; n&oacute; chỉ chiếm 2% trong tổng số c&aacute;c email lừa đảo. Ngo&agrave;i tấn c&ocirc;ng lừa đảo, đối tượng xấu c&ograve;n lợi dụng Covid-19 theo c&aacute;c c&aacute;ch kh&aacute;c, chẳng hạn Zoombombing &ndash; tấn c&ocirc;ng v&agrave;o c&aacute;c cuộc họp trực tuyến để l&agrave;m gi&aacute;n đoạn hội nghị. Du Lam (Theo BI)', '10_microsoft_warning_hacker.jpg', '2020-03-28', NULL, NULL, NULL, NULL);
INSERT INTO `tintucs` (`tinTuc_id`, `tenTinTuc`, `moTa`, `tomTat`, `noiDung`, `Hinh`, `ngayDang`, `hocVien_id`, `giangVien_id`, `created_at`, `updated_at`) VALUES
(11, 'uhhhhh', 'Tin Công Nghệ (Nguồn: ICTNews)', 'Theo Bộ trưởng Bộ TT&amp;TT Nguyễn Mạnh H&ugrave;ng, Việt Nam phải l&agrave;m chủ được hạ tầng số, tương tự như những g&igrave; đ&atilde; l&agrave;m được với ng&agrave;nh viễn th&ocirc;ng. Đ&acirc;y ch&iacute;nh l&agrave; cơ hội để th&uacute;c đẩy Make in Vietnam.', '<h2>Giảm gi&aacute; điện to&aacute;n đ&aacute;m m&acirc;y, k&iacute;ch cầu chuyển đổi số</h2>\r\n\r\n<p>Để k&iacute;ch cầu thị trường, g&oacute;p phần th&uacute;c đẩy ph&aacute;t triển kinh tế, hỗ trợ c&aacute;c cơ quan, doanh nghiệp chuyển đổi hoạt động l&ecirc;n m&ocirc;i trường mạng, Bộ Th&ocirc;ng tin v&agrave; Truyền th&ocirc;ng (TT&amp;TT) đ&atilde; tổ chức &ldquo;Lễ ph&aacute;t động chiến dịch th&uacute;c đẩy chuyển đổi số bằng nền tảng điện to&aacute;n đ&aacute;m m&acirc;y Việt Nam&rdquo;.</p>\r\n\r\n<p>Theo đ&aacute;nh gi&aacute; của c&aacute;c chuy&ecirc;n gia, trong giai đoạn phục hồi sau đại dịch Covid-19, chuyển đổi số sẽ gi&uacute;p c&aacute;c doanh nghiệp Việt Nam, đặc biệt l&agrave; doanh nghiệp vừa v&agrave; nhỏ t&igrave;m kiếm một m&ocirc; h&igrave;nh hoạt động kinh doanh linh hoạt hơn. Điều n&agrave;y vừa gi&uacute;p doanh nghiệp tiết giảm chi ph&iacute;, vừa tối ưu nguồn lực để vượt qua kh&oacute; khăn.&nbsp;</p>\r\n\r\n<p>Một trong những trụ cột quan trọng của chuyển đổi số phải kể đến c&ocirc;ng nghệ điện to&aacute;n đ&aacute;m m&acirc;y (ĐTĐM). Hiện c&aacute;c doanh nghiệp c&ocirc;ng nghệ h&agrave;ng đầu Việt Nam đều đ&atilde; chuyển dịch theo xu hướng n&agrave;y v&agrave; nhu cầu khai th&aacute;c ĐTĐM cũng đang tăng trưởng mạnh.&nbsp;<br />\r\n&nbsp;</p>\r\n\r\n<p>Thị trường ĐTĐM trong nước hiện nay đạt khoảng 133 triệu USD, tương đương 3,200 tỷ đồng. Việt Nam c&oacute; khoảng 27 trung t&acirc;m dữ liệu do 11 doanh nghiệp trong nước đầu tư với tr&ecirc;n 270.000 m&aacute;y chủ được kết nối đến cả nước.</p>\r\n\r\n<p>Theo &ocirc;ng Vũ Minh Tr&iacute; - Tổng gi&aacute;m đốc VNG CLOUD, Chủ nhiệm C&acirc;u lạc bộ Điện to&aacute;n đ&aacute;m m&acirc;y v&agrave; Trung t&acirc;m dữ liệu Việt Nam, yếu tố vượt trội của doanh nghiệp đ&aacute;m m&acirc;y trong nước nằm ở sự sẵn s&agrave;ng của đội ngũ kỹ sư đ&ocirc;ng đảo v&agrave; khả năng đ&agrave;o tạo cho kh&aacute;ch h&agrave;ng sử dụng ứng dụng tại chỗ. Tuy vậy, c&aacute;c doanh nghiệp Việt Nam mới chỉ chiếm được 20% thị phần, 80% vẫn l&agrave; d&ugrave;ng đ&aacute;m m&acirc;y đặt tại nước ngo&agrave;i.</p>\r\n\r\n<p>Thực tế n&agrave;y đ&ograve;i hỏi c&aacute;c doanh nghiệp ĐTĐM tại Việt Nam phải li&ecirc;n minh lại với nhau. Do đ&oacute;, ngay tại sự kiện, &ocirc;ng Nguyễn Trung Ch&iacute;nh - Chủ tịch HĐQT Tập đo&agrave;n CMC đ&atilde; thay mặt cộng đồng c&aacute;c doanh nghiệp cung cấp hạ tầng số, chuyển đổi số ch&iacute;nh thức c&ocirc;ng bố sự ra mắt của li&ecirc;n minh hạ tầng điện to&aacute;n đ&aacute;m m&acirc;y Việt nam.<br />\r\n<br />\r\n&nbsp;</p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, 11 doanh nghiệp điện to&aacute;n đ&aacute;m m&acirc;y Việt Nam đ&atilde; cam kết tham gia chiến dịch ph&aacute;t động của Bộ T&amp;TT, giảm gi&aacute; 20% cho tất cả kh&aacute;ch h&agrave;ng đăng k&yacute; mới dịch vụ điện to&aacute;n đ&aacute;m m&acirc;y Việt Nam trong thời gian 2 th&aacute;ng (từ 22/5-22/7).&nbsp;</p>\r\n\r\n<p>Cũng tại sự kiện, &ocirc;ng Nguyễn Khắc Lịch, Ph&oacute; Cục trưởng Cục An to&agrave;n th&ocirc;ng tin (Bộ TT&amp;TT) đ&atilde; c&ocirc;ng bố Bộ ti&ecirc;u ch&iacute; đ&aacute;nh gi&aacute; c&aacute;c doanh nghiệp điện to&aacute;n đ&aacute;m m&acirc;y đạt chuẩn để c&aacute;c doanh nghiệp, cơ quan nh&agrave; nước sử dụng.</p>\r\n\r\n<h2>Việt Nam phải l&agrave;m chủ hạ tầng chuyển đổi số</h2>\r\n\r\n<p>Đ&oacute; l&agrave; lời khẳng định của Bộ trưởng Bộ TT&amp;TT Nguyễn Mạnh H&ugrave;ng tại Lễ ph&aacute;t động chiến dịch th&uacute;c đẩy chuyển đổi số bằng nền tảng điện to&aacute;n đ&aacute;m m&acirc;y. Theo đ&oacute;, Việt Nam phải l&agrave;m chủ c&aacute;c hạ tầng v&agrave; nền tảng chuyển đổi số cho từng ng&agrave;nh, từng lĩnh vực. Đ&acirc;y ch&iacute;nh l&agrave; cơ hội cho&nbsp;Make in Vietnam.&nbsp;</p>', 'JmFo_12_symbolfoto-symbolbild-der-neuen-datenspende-app-des-robert-koch-institut.jpg', '2020-05-25', NULL, NULL, NULL, NULL);

